import React, { useState } from 'react';

const CandidatePanel = ({ candidates, onSelectCandidates, disabled = false }) => {
  const [selectedCandidates, setSelectedCandidates] = useState(new Set());

  // Handle individual candidate selection
  const handleCandidateSelect = (candidateId) => {
    const newSelected = new Set(selectedCandidates);
    if (newSelected.has(candidateId)) {
      newSelected.delete(candidateId);
    } else {
      newSelected.add(candidateId);
    }
    setSelectedCandidates(newSelected);
  };

  // Handle select all toggle
  const handleSelectAll = () => {
    if (selectedCandidates.size === candidates.length) {
      // Deselect all
      setSelectedCandidates(new Set());
    } else {
      // Select all
      setSelectedCandidates(new Set(candidates.map(c => c.id)));
    }
  };

  // Handle proceed to interviews
  const handleProceed = () => {
    const selectedIds = Array.from(selectedCandidates);
    onSelectCandidates(selectedIds);
  };

  // Get score color based on score value
  const getScoreColor = (score) => {
    if (score >= 90) return 'linear-gradient(135deg, #4CAF50, #45a049)'; // Green
    if (score >= 80) return 'linear-gradient(135deg, #8BC34A, #689F38)'; // Light Green
    if (score >= 70) return 'linear-gradient(135deg, #FFC107, #FF8F00)'; // Orange
    if (score >= 60) return 'linear-gradient(135deg, #FF9800, #F57C00)'; // Dark Orange
    if (score > 0) return 'linear-gradient(135deg, #FF5722, #D84315)'; // Red
    return 'linear-gradient(135deg, #9E9E9E, #616161)'; // Gray for failed analysis
  };

  // Get rank suffix
  const getRankSuffix = (rank) => {
    if (rank === 1) return 'st';
    if (rank === 2) return 'nd';
    if (rank === 3) return 'rd';
    return 'th';
  };

  // Sort candidates by score (highest first), then by name for failed analyses
  const sortedCandidates = [...candidates].sort((a, b) => {
    const scoreA = a.score || 0;
    const scoreB = b.score || 0;

    if (scoreA !== scoreB) {
      return scoreB - scoreA; // Higher scores first
    }

    // If scores are equal (especially for failed analyses), sort by name
    const nameA = a.name || 'Unknown';
    const nameB = b.name || 'Unknown';
    return nameA.localeCompare(nameB);
  });

  if (!candidates || candidates.length === 0) {
    return (
      <div className="candidate-panel">
        <div className="panel-header">
          <h2>👥 Candidate Rankings</h2>
        </div>
        <div className="no-candidates">
          <p>No candidates processed yet. Please upload resumes first.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="candidate-panel">
      <div className="panel-header">
        <h2>👥 Candidate Rankings</h2>
        <div className="panel-actions">
          <button 
            onClick={handleSelectAll}
            className="select-all-btn"
            disabled={disabled}
          >
            {selectedCandidates.size === candidates.length ? '❌ Deselect All' : '✅ Select All'}
          </button>
          <button 
            onClick={handleProceed}
            className="proceed-btn"
            disabled={disabled || selectedCandidates.size === 0}
          >
            📅 Schedule Interviews ({selectedCandidates.size})
          </button>
        </div>
      </div>

      <div className="candidates-list">
        {sortedCandidates.map((candidate, index) => {
          const isSelected = selectedCandidates.has(candidate.id);
          const score = candidate.score || 0;
          const rank = index + 1;
          const hasAnalysisFailed = score === 0 || candidate.summary?.includes('Analysis failed');

          return (
            <div 
              key={candidate.id} 
              className={`candidate-card ${isSelected ? 'selected' : ''}`}
              onClick={() => !disabled && handleCandidateSelect(candidate.id)}
              style={{ cursor: disabled ? 'not-allowed' : 'pointer' }}
            >
              <div className="candidate-header">
                <div className="candidate-info">
                  <input
                    type="checkbox"
                    checked={isSelected}
                    onChange={(e) => {
                      e.stopPropagation();
                      if (!disabled) handleCandidateSelect(candidate.id);
                    }}
                    disabled={disabled}
                  />
                  <div>
                    <h3>{candidate.name || 'Unknown Candidate'}</h3>
                    {candidate.email && (
                      <div className="candidate-email">{candidate.email}</div>
                    )}
                    {candidate.phone && (
                      <div className="candidate-phone">{candidate.phone}</div>
                    )}
                  </div>
                </div>
                <div className="candidate-score">
                  <div 
                    className="score-badge"
                    style={{ 
                      background: getScoreColor(score),
                      opacity: hasAnalysisFailed ? 0.7 : 1
                    }}
                  >
                    {hasAnalysisFailed ? '?' : score}
                  </div>
                  <div className="rank">
                    {rank}{getRankSuffix(rank)} Place
                  </div>
                </div>
              </div>

              <div className="candidate-details">
                <div className="summary">
                  <h4>📋 Summary</h4>
                  <p>
                    {candidate.summary || 'No summary available'}
                    {hasAnalysisFailed && (
                      <span style={{ color: '#ff6b6b', fontStyle: 'italic' }}>
                        <br />⚠️ AI analysis failed - manual review recommended
                      </span>
                    )}
                  </p>
                </div>

                <div className="skills">
                  <h4>🛠️ Matching Skills</h4>
                  {candidate.skills && candidate.skills.length > 0 ? (
                    <div className="skills-tags">
                      {candidate.skills.map((skill, skillIndex) => (
                        <span key={skillIndex} className="skill-tag">
                          {skill}
                        </span>
                      ))}
                    </div>
                  ) : (
                    <p style={{ fontStyle: 'italic', color: '#6b7280' }}>
                      No specific skills listed
                    </p>
                  )}
                </div>

                <div className="experience">
                  <h4>💼 Experience</h4>
                  <p>{candidate.experience || 'Experience information not available'}</p>
                </div>

                {hasAnalysisFailed && (
                  <div className="manual-review-note" style={{
                    background: 'linear-gradient(135deg, rgba(255, 107, 107, 0.1), rgba(238, 90, 82, 0.1))',
                    padding: '1rem',
                    borderRadius: '8px',
                    border: '1px solid rgba(255, 107, 107, 0.3)',
                    marginTop: '1rem'
                  }}>
                    <h4 style={{ color: '#ff6b6b', margin: '0 0 0.5rem 0' }}>🔍 Manual Review Required</h4>
                    <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
                      The AI analysis for this candidate failed. Please review the resume manually 
                      to determine suitability for the position.
                    </p>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* Selection Summary */}
      <div className="selection-summary" style={{
        background: 'white',
        padding: '1.5rem',
        borderRadius: '12px',
        marginTop: '2rem',
        boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
        border: '1px solid rgba(0,0,0,0.05)'
      }}>
        <h3 style={{ margin: '0 0 1rem 0', color: '#333' }}>📊 Selection Summary</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '1.5rem', fontWeight: 'bold', color: '#667eea' }}>
              {candidates.length}
            </span>
            <span style={{ fontSize: '0.9rem', color: '#6b7280' }}>Total Candidates</span>
          </div>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '1.5rem', fontWeight: 'bold', color: '#4CAF50' }}>
              {selectedCandidates.size}
            </span>
            <span style={{ fontSize: '0.9rem', color: '#6b7280' }}>Selected for Interview</span>
          </div>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '1.5rem', fontWeight: 'bold', color: '#FF9800' }}>
              {candidates.filter(c => (c.score || 0) >= 70).length}
            </span>
            <span style={{ fontSize: '0.9rem', color: '#6b7280' }}>High Scores (70+)</span>
          </div>
          <div style={{ textAlign: 'center' }}>
            <span style={{ display: 'block', fontSize: '1.5rem', fontWeight: 'bold', color: '#9E9E9E' }}>
              {candidates.filter(c => (c.score || 0) === 0).length}
            </span>
            <span style={{ fontSize: '0.9rem', color: '#6b7280' }}>Need Manual Review</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidatePanel;
